CREATE TABLE Users(
  userID INT NOT NULL AUTO_INCREMENT,
  firstName VARCHAR(255) NOT NULL,
  lastName VARCHAR(255) NOT NULL,
  userName VARCHAR(255) UNIQUE NOT NULL,
  password VARCHAR(255) NOT NULL,
  email VARCHAR(255) NOT NULL,
  image LONGTEXT,
  permission INT NOT NULL,
  authtoken VARCHAR(255),
  PRIMARY KEY(userID)
);  


CREATE TABLE Places(
  placeID INT NOT NULL AUTO_INCREMENT,
  placeName VARCHAR(255) UNIQUE NOT NULL,
  description VARCHAR(255) NOT NULL,
  phoneNumber VARCHAR(255) DEFAULT "-",
  image LONGTEXT,
  gpsX FLOAT( 10, 6 ),
  gpsY FLOAT( 10, 6 ),
  visible INT DEFAULT 1 NOT NULL,
  PRIMARY KEY(placeID)
);  

CREATE TABLE Burgers(
  burgerID INT NOT NULL AUTO_INCREMENT,
  burgerName VARCHAR(255) UNIQUE NOT NULL,
  placeID INT NOT NULL,
  description LONGTEXT NOT NULL,
  image  LONGTEXT,
  visible INT DEFAULT 1 NOT NULL,
  FOREIGN KEY (placeID) REFERENCES Places(placeID),
  PRIMARY KEY(burgerID)
);  

CREATE TABLE Ratings(
  ratingID INT NOT NULL AUTO_INCREMENT,
  burgerID INT NOT NULL,
  userID INT NOT NULL,
  pTaste INT NOT NULL,
  pPrepareTime INT NOT NULL,
  pApperance INT NOT NULL,
  pTotal DOUBLE NOT NULL,
  comment VARCHAR(255) NOT NULL,
  likes INT DEFAULT 0 NOT NULL,
  date TIMESTAMP,
  visible INT DEFAULT 1 NOT NULL,
  FOREIGN KEY (burgerID) REFERENCES Burgers(burgerID),
  FOREIGN KEY (userID) REFERENCES Users(userID),
  PRIMARY KEY(ratingID),
  UNIQUE KEY (userID,burgerID)
);


CREATE TABLE Likes(
  likeID INT NOT NULL AUTO_INCREMENT,
  userID INT NOT NULL,
  ratingID INT NOT NULL,
  burgerID INT NOT NULL,
  FOREIGN KEY (userID) REFERENCES Users(userID),
  FOREIGN KEY (ratingID) REFERENCES Ratings(ratingID),
  FOREIGN KEY (burgerID) REFERENCES Burgers(burgerID),
  PRIMARY KEY(likeID),
  UNIQUE KEY (userID,ratingID)
);

